import React, { Component } from "react";

const BpmnJS = window.BpmnJS;
const $ = window.$;


const bpmnXML = `<?xml version="1.0" encoding="UTF-8"?>
<definitions xmlns="http://www.omg.org/spec/BPMN/20100524/MODEL" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance">
  <process id="theProcess" isExecutable="true">
  <startEvent id="theStart" />
  <scriptTask id="scriptTask" scriptFormat="Javascript">
    <script>
      <![CDATA[
        const request = services.request;
        const self = this;
        request.get('https://raw.githubusercontent.com/transmute-industries/github-did/master/dids/1bed11140547b8407478bdf2650db50a5a0c18ef2ae4caf20e818a9433923c2a.jsonld', (err, resp, body) => {
          if (err) return next(err);
          self.variables.scriptTaskCompleted = true;
          next(null, {result: body});
        })
      ]]>
    </script>
  </scriptTask>
  <endEvent id="theEnd" />
  <sequenceFlow id="flow1" sourceRef="theStart" targetRef="scriptTask" />
  <sequenceFlow id="flow2" sourceRef="scriptTask" targetRef="theEnd" />
  </process>
</definitions>`

class Modeler extends Component {
  componentWillMount() {
    setTimeout(() => {
      var diagramUrl =
        "/diagram.bpmn";

      const bpmnModeler = new BpmnJS({
        container: "#canvas",
        keyboard: {
          bindTo: window
        }
      });

      this.bpmnModeler = bpmnModeler;

      $.get(diagramUrl, openDiagram, "text");
      // openDiagram(bpmnXML)

      function openDiagram(bpmnXML) {
        // import diagram
        bpmnModeler.importXML(bpmnXML, function(err) {
          if (err) {
            return console.error("could not import BPMN 2.0 diagram", err);
          }
          // access modeler components
          var canvas = bpmnModeler.get("canvas");
          var overlays = bpmnModeler.get("overlays");
          // zoom to fit full viewport
          canvas.zoom("fit-viewport");
          // attach an overlay to a node
          overlays.add("SCAN_OK", "note", {
            position: {
              bottom: 0,
              right: 0
            },
            html: '<div class="diagram-note">Mixed up the labels?</div>'
          });
          // add marker
          canvas.addMarker("SCAN_OK", "needs-discussion");
        });
      }
    }, 1 * 1000);
  }

  exportDiagram = () => {
    this.bpmnModeler.saveXML({ format: true }, function(err, xml) {
      if (err) {
        return console.error("could not save BPMN 2.0 diagram", err);
      }
      alert("Diagram exported. Check the developer tools!");
      console.log("DIAGRAM", xml);
    });
  };
  render() {
    return (
      <div id="modeler">
        <div id="canvas" />
        <button
          id="save-button"
          onClick={() => {
            this.exportDiagram();
          }}
        >
          print to console
        </button>
      </div>
    );
  }
}

export default Modeler;
